var odbc = require('../')({ connectTimeout : 2, loginTimeout : 2 });
var cn = "DRIVER={FreeTDS};SERVERNAME=test;DATABASE=test;UID=test;PWD=test;AutoTranslate=yes";
//var cn = "DRIVER={SQL Server Native Client 11.0};SERVER=24.103.152.224;DATABASE=test;UID=test;PWD=test;";

console.log('opening connection');
  odbc.open(cn, function (err) {
    if (err) return console.log(err);

    var sql = 'exec dbo.qry_mytable @id=?';
    var params = [2]; 

    odbc.query(sql, params, function (err, data, more) {
      if (err) console.log(err);

      console.log('more', more);

      if (!more) {
        odbc.close(function () {
          console.log('done');
        });
      }
    });
  });  
